function loadTrainingData(directory)    subDirs = ['00'; '01'; '10'; '11'; '20'; '21'; '30'; '31'; '40'; '41'; '50'; '51'; '60'; '61'; '70'; '71'; '80'; '81'; '90'; '91'];  X = zeros(1,1600);      for n = 1: size(subDirs,1)    for i = 1:70      suffix = subDirs(n, :);      fileName = strcat(directory, suffix);      fileName = strcat(fileName, '/');      fileName = strcat(fileName, num2str(i));      fileName = strcat(fileName, '.jpg');      fprintf('Saving data for %s\n', fileName);      temp = loadImage(fileName);      X = [X ; temp];      fprintf('Size of X is : %d', size(X));      y = substr(suffix, 1, 1)      save('trainingData.mat', 'X', '-append');      save('trainingData.mat', 'y', '-append');    endfor    endfor    fprintf('Successfully saved all training image data\n');  
endfunction
