function normVec = normalizeVector(vec)    max = max(vec);  min = min(vec);  diff = max-min;  m = size(vec,2);  tempVec = vec';  normVec = zeros(size(tempVec));  size(normVec);  for i = 1 : m#    fprintf('Value of i is %d and vec(%d) is %d\n', i, i, tempVec(i));    normVec(i) = (2*double(tempVec(i) - min)/double(diff))-1;#    normVec(i) = (255*double(tempVec(i) - min)/double(diff));#    normVec(i) = normVec(i)/diff;#    normVec(i) = 2*normVec(i,1);#    normVec(i) = normVec(i,1) - 1;#    fprintf('normVec(%d) is %d\n', i, normVec(i));  endfor  normVec = normVec';  
endfunction
